package qc.UnityDriver.DataFonctionAdd;import java.awt.List;import java.util.ArrayList;import org.bukkit.Material;import org.bukkit.enchantments.Enchantment;import org.bukkit.inventory.ItemStack;import org.bukkit.inventory.meta.ItemMeta;public class ItemBuilder { public static ItemBuilder instance; public static ItemBuilder getItemBuilder(){ return instance;}private Material ItemType; private int ItemInt; private String ItemName;private String ItemLore;private Enchantment ItemEnch;private int ItemEnchPower;private short ItemData;public ItemBuilder(Material ItemType, int ItemInt, short ItemData){instance = this;this.ItemType = ItemType;this.ItemInt = ItemInt;this.ItemData = ItemData;this.ItemLore = "";}public ItemBuilder(Material ItemType, int ItemInt, String ItemName,short ItemData){this.ItemType = ItemType;this.ItemInt = ItemInt;		this.ItemName = ItemName;this.ItemData = ItemData;this.ItemLore = "";}public ItemBuilder(Material ItemType, int ItemInt, String ItemName,String ItemLore,short ItemData){this.ItemType = ItemType;this.ItemInt = ItemInt;this.ItemName = ItemName;this.ItemLore = ItemLore;this.ItemData = ItemData;}public ItemBuilder(Material ItemType, int ItemInt, String ItemName,String ItemLore, Enchantment ItemEnch, int ItemEnchPower,short ItemData) {this.ItemType = ItemType;this.ItemInt = ItemInt;this.ItemName = ItemName;this.ItemLore = ItemLore;this.ItemEnch = ItemEnch;this.ItemEnchPower = ItemEnchPower;this.ItemData = ItemData;}public ItemStack setItem() {ItemStack ri = new ItemStack(this.ItemType, this.ItemInt, this.ItemData);ItemMeta rim = ri.getItemMeta();ArrayList<String> l = new ArrayList();l.add(this.ItemLore.replaceAll("&","ยง"));rim.setDisplayName(this.ItemName.replaceAll("&","ยง"));rim.setLore(l);if(!(ItemEnch == null)) {rim.addEnchant(ItemEnch, ItemEnchPower, true);}ri.setItemMeta(rim);return ri;}}
